<?php/* * @FILE DESCRIPTION: Handles the users data (the user logged in) saves time from re-writing the query per page. */$aUser = isset($_SESSION['user']); // obtains the users username when in-session$avail = ""; // Code will over write with "yes" or left blank by the server.require('SQL.php'); // Make the connection to the database$res = mysqli_query($sql, "SELECT Username FROM accounts"); // List accounts from the databasewhile ($row = mysqli_fetch_array($res)) // Loops through all the accounts{	if ($aUser == $row['Username']) // does "aUser" match a username found in the query?	{		$avail = "yes"; // avail will be changed to yes if a match was found.	}}if($avail == "yes") { // Run the below code if a match was found.$userquery = mysqli_query($sql, "SELECT * FROM accounts WHERE Username ='".$_SESSION['user']."' LIMIT 1"); // Select all the users information from the databaseif (!$userquery) { // Checks for errors if yes "die" will cancel the request and display an error message.    die('Invalid query: ' . mysqli_error($sql));}$user = mysqli_fetch_array($userquery, MYSQL_ASSOC); // assign global variable "$user" where we can fetch their information to be displayed on the web page.}else // $avail didn't match "yes" (user wasn't found) then it will display the below message and query will be canceled.{ 	echo "You are not logged in."; }?>